import NavBar from "../components/NavBar";
import { useState, useEffect, useRef } from "react";
import { UserContext } from "../contexts/UserContext";
import Cookies from 'js-cookie';
import { useNavigate } from 'react-router-dom'
import '../Styles/Account.css';
import DeleteConfirmation from "../components/DeleteConfirmation";



export const validateValues = (inputValues) => {
    let errors = {};

    if (!inputValues.oldPassword)
        errors.oldPassword = "Old password is required";

    if (!inputValues.newPassword)
        errors.newPassword = "New password is required";
    else if (inputValues.newPassword.length < 8)
        errors.newPassword = "Password is short"
    if (!inputValues.repassword)
        errors.repassword = "Verify the Password";
    else if (inputValues.newPassword != inputValues.repassword) {
        errors.repassword = "Passwords do not match";
    }

    return errors;
};


export default function Account() {
    const navigate = useNavigate();


    const [authenticated, setAuthenticated] = useState(null);
    const [inputFields, setInputFields] = useState({
        name: "",
        surname: ""
    });
    const [errors, setErrors] = useState({
        name: "",
        surname: ""
    });
    const [userInfo, setUserInfo] = useState({
        name: "",
        surname: "",
        email: ""
    });
    const [submitting, setSubmitting] = useState(false);
    const [formMessage, setFormMessage] = useState(null);
    const [showConfirmation, setShowConfirmation] = useState(false);
    const [blur, setBlur] = useState(false);
    const [readOnly, setReadOnly] = useState(true);



    useEffect(() => {
        setAuthenticated(Cookies.get("authenticated") === "true" ? true : false);
    }, []);
    useEffect(() => {
        GetCurrentUser();
    }, []);


    const GetCurrentUser = async () => {
        const requestOptions = {
            method: "GET",
            headers: { "Content-Type": "application/json", 'Authorization': 'Bearer ' + Cookies.get("token").toString() },
        };
        const response = await fetch("http://localhost:3000/users/current", requestOptions);
        const data = await response.json();

        setInputFields(data);
    }
    const DeleteAccount = async () => {
        const requestOptions = {
            method: "DELETE",
            headers: { "Content-Type": "application/json", 'Authorization': 'Bearer ' + Cookies.get("token").toString() },
        };
        const response = await fetch("http://localhost:3000/users/current", requestOptions);
        const data = await response.json();

        if (response.ok) {
            Cookies.set("authenticated", false);
            Cookies.set("token", "");
            Cookies.set("email", false);
            Cookies.set("name", "");
            Cookies.set("surname", "");

            navigate("/SignIn");
        }
    }


    const handleDeleteClick = () => {
        setShowConfirmation(true);
        setBlur(true);
    };

    const handleCancel = () => {
        setShowConfirmation(false);
        setBlur(false);
    };

    const handleConfirm = () => {
        DeleteAccount();
    };

    const handleEditClick = () => {
        setReadOnly(false);
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setInputFields({ ...inputFields, [name]: value });
    };
    const handleSubmit = (event) => {
        event.preventDefault();
        setErrors(validateValues(inputFields));
        setSubmitting(true);
    };
    useEffect(() => {
        if (Object.keys(errors).length === 0 && submitting) {

        }
    }, [errors]);


    if (authenticated) {
        return (
            <>
                <div className={blur ? "blur" : null}>
                    <NavBar />
                    <h1 className="h1">User Profile</h1>
                    <div className="form">
                        <div>
                            <div className="input-error-container">
                                <div className="input-label-container">
                                    <input type="text" name="name" className="input" value={inputFields.name} onChange={handleChange} readOnly={readOnly} />
                                    <label className="label">Name</label>
                                </div>
                                <label className="error">{errors.name}</label>
                            </div>

                            <div className="input-error-container">
                                <div className="input-label-container">
                                    <input type="text" name="surname" className="input" value={inputFields.surname} onChange={handleChange} readOnly={readOnly} />
                                    <label className="label">Surname</label>
                                </div>
                                <label className="error">{errors.surname}</label>
                            </div>

                            <div className="input-error-container">
                                <div className="input-label-container">
                                    <input type="text" name="email"
                                        className="input" value={inputFields.email} onChange={handleChange} readOnly />
                                    <label className="label">Email</label>
                                </div>
                                <label className="error">{errors.email}</label>
                            </div>
                            <div className="account-buttons-container">
                                <button class="edit-user-button" onClick={handleEditClick}>
                                    <span class="text">Edit Account</span>
                                </button>
                                <button class="delete-user-button" onClick={handleDeleteClick}>
                                    <span class="text">Delete Account</span>
                                    <span class="icon">
                                        <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24">
                                            <path d="M24 20.188l-8.315-8.209 8.2-8.282-3.697-3.697-8.212 8.318-8.31-8.203-3.666 3.666 8.321 8.24-8.206 8.313 3.666 3.666 8.237-8.318 8.285 8.203z" />
                                        </svg>
                                    </span>
                                </button>
                            </div>
                        </div>
                    </div >

                </div>
                <div>
                    {
                        showConfirmation && (<DeleteConfirmation onCancel={handleCancel} onConfirm={handleConfirm} />)
                    }
                </div>
            </>
        )
    }
    else {
        navigate("/SignIn");
    }
}
